tinymce.PluginManager.add("fieldinsert",function(editor){var items=[];tinymce.each(editor.settings.fieldinsert_list||["","No Fields","There are no fields"],function(field){items.push({type:"menuitem",text:field[1],onAction:function(){editor.insertContent(field[0]);}});});editor.ui.registry.addMenuButton("fieldinsert",{text:"Insert Field",icon:"template",title:"Insert field",fetch:function(callback){callback(items);}});editor.ui.registry.addNestedMenuItem("fieldinsert",{icon:"template",text:"Insert field",getSubmenuItems:function(){return items;}});});